//let databaseResourceName = '{DatabaseResourceName}';
//Weighted query steps
let RequestSteps = SynapseSqlPoolRequestSteps
//| where _ResourceId == databaseResourceName
//| where RowCount > 10000000
| extend Step_Elapsed_Min = round((EndTime - StartTime) /1m, 2)
| extend Weighted_cnt = iff('{WeightParameter}'=='Weighted',(case(OperationType == 'BroadcastMoveOperation', (RowCount*10), RowCount)),RowCount)
| project-rename StepStatus = Status, StepStart = StartTime, StepEnd = EndTime//, StepResource = Resource
| top 100 by Weighted_cnt desc
| order by Weighted_cnt desc
| project Request_ID=RequestId,OperationType, RowCount,Weighted_cnt,Step_Elapsed_Min, StepStart, StepEnd, StepStatus, StepIndex;
RequestSteps | join (
SynapseSqlPoolExecRequests
//| where _ResourceId == databaseResourceName
| where StatementType !in ('Batch','Execute')
| summarize //Session_ID=any(SessionId),
    Request_ID=any(RequestId),
    //Submit_Time=max(SubmitTime),
    Start_Time=max(StartTime),
    End_Time=max(EndTime),
    Command=any(Command),
    Status=min(Status), //This works because Completed/Failed/Cancelled are all below running, below suspended. This happens to work out where the min is the current status
    Statement_Type=any(StatementType),
    Resource_class=any(ResourceClass)
    by RequestId
| summarize //Session_ID=any(Session_ID), 
//Submit_Time=any(Submit_Time) ,
Start_Time=any(Start_Time), End_Time=any(End_Time),Command=any(Command),Status=any(Status),Statement_Type=any(Statement_Type),Resource_class=any(Resource_class) by Request_ID
//| order by Step_Elapsed_Min desc
| extend Request_Elapsed_Min = round((End_Time - Start_Time)/1m, 2)
| project-rename ReqStatus = Status, ReqStart = Start_Time, ReqEnd = End_Time
) on Request_ID
| project-reorder //Session_ID, 
Request_ID, Request_Elapsed_Min, ReqStart, ReqEnd, ReqStatus,StepIndex, OperationType, RowCount,Weighted_cnt,Step_Elapsed_Min, StepStart, StepEnd, StepStatus, Statement_Type, Command, Resource_class
| order by Weighted_cnt desc
| limit 20

//Build cache count by day
let databaseResourceName = '{DatabaseResourceName}';
//Long-Running Queries - Just Actual Queries
//If 'Command' is empty - you need to set a bigger time window, the older record with the command is outside of your window
SynapseSqlPoolExecRequests
| where _ResourceId  == databaseResourceName
| where StatementType == 'BuildReplicatedTableCache'
| where Command != ""
| where ResourceClass != ""
| project Command,TimeGenerated,RequestId
| summarize LastRebuild=max(TimeGenerated),RebuildCount=count(RequestId) by Command
| order by RebuildCount desc
| limit 15

//Build cache count by day
let databaseResourceName = '{DatabaseResourceName}';
//Long-Running Queries - Just Actual Queries
//If 'Command' is empty - you need to set a bigger time window, the older record with the command is outside of your window
SynapseSqlPoolExecRequests
| where _ResourceId  == databaseResourceName
| where StatementType == 'BuildReplicatedTableCache'
| where Command != ""
| where ResourceClass != ""
| project Command,TimeGenerated,RequestId
| summarize count() by Command, bin(TimeGenerated,24h)
//| where count_ > 7
| order by count_ desc
